import { convert } from '../convert'
import { ConvertInput } from '../type'

describe('convert', () => {
  it('flip x axis', async () => {
    const input = {
      image:
        'data:image/jpeg;base64,',
      flip: {
        x: true,
        y: false,
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('flip y axis', async () => {
    const input = {
      image:
        'data:image/jpeg;base64,',
      flip: {
        x: false,
        y: true,
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('flip x and y axis', async () => {
    const input = {
      image:
        'data:image/jpeg;base64,',
      flip: {
        x: true,
        y: true,
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('rotate 45 degree', async () => {
    const input = {
      image:
        'data:image/jpeg;base64,',
      rotate: {
        angle: 45
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('edit the sharpen', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      sharpen: {
        options: {
          sigma: 2,
          m1: 1,
          m2: 1,
          x1: 9,
          y2: 10,
          y3: 11
        }
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('edit the median', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      median: {
        size: 50
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('edit the blur', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      blur: {
        sigma: 75
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('edit the flatten', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      flatten: {
        options: {
          background: "red"
        }
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('enable the nagate', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      negate: {
        options: {
          alpha: true
        }
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('enable the normalise', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      normalise: true
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it.skip('Convert file Format to gif', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      format: "gif"
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('Convert file Format to JPEG', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      format: "jpeg"
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('Convert file Format to PNG', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      format: "png"
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('Convert file Format to TIFF', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      format: "tiff"
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('Convert file Format to WEBP', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      format: "webp"
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('resize the picture', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      resize: {
        width: 100,
        height: 100,
        fit: "fill"
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('edit the tint', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      tint: {
        rgb: "#111"
      },
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })

  it('enable the greyscale', async () => {
    const input: ConvertInput = {
      image:
        'data:image/jpeg;base64,',
      greyscale: true
    }
    const output = await convert(input)
    expect(output).toMatchSnapshot()
  })


})

export {}
